var menu = document.querySelector(".menu"), menuInner = document.querySelector(".menu__inner"), body = document.querySelector("body"); menu.addEventListener("click", () => { menu.classList.toggle("active"), menuInner.classList.toggle("menu-active"), body.classList.toggle("lock") }); const sliders = document.querySelectorAll(".swiper"); sliders.forEach(e => { new Swiper(e, { loop: !0, spaceBetween: 50, pagination: { el: e.querySelector(".swiper-pagination"), clickable: !0 }, navigation: { nextEl: e.querySelector(".swiper-button-next"), prevEl: e.querySelector(".swiper-button-prev") } }) });